#!/bin/bash

groupadd --gid 777 cs-ansible
useradd --create-home --home-dir /var/cs-ansible --password '*' --comment 'CoSci Local Ansible User' --gid cs-ansible --shell /bin/bash --uid 777 cs-ansible
mkdir -p ~cs-ansible/.ssh && chmod 700 ~cs-ansible/.ssh && touch ~cs-ansible/.ssh/authorized_keys && chmod 600 ~cs-ansible/.ssh/authorized_keys && echo "ssh-rsa  root@ansible-control" >> ~cs-ansible/.ssh/authorized_keys && chown -R cs-ansible:cs-ansible ~cs-ansible/.ssh
echo 'cs-ansible ALL=(root) NOPASSWD: ALL' > /etc/sudoers.d/cs-ansible && chmod --reference=/etc/sudoers /etc/sudoers.d/cs-ansible && chown --reference=/etc/sudoers /etc/sudoers.d/cs-ansible
